!function(){let hovBtn=document.querySelectorAll("[data-hover-target]");function classAdding(element){element.classList.add("active")}function classRemove(element){element.classList.remove("active")}hovBtn.forEach(button=>{button.addEventListener("mouseenter",()=>{let element;classAdding(document.querySelector(button.dataset.hoverTarget))})}),hovBtn.forEach(button=>{button.addEventListener("mouseleave",()=>{let element;classRemove(document.querySelector(button.dataset.hoverTarget))})})}();